###### MENU PART
def menu_choice_checker(choice)
	case choice
	when "1"
		return "count_total_journalists"
	when "2"
		return "count_names_with_number"
	when "3"
		return "count_names_with_aude"
	when "4"
		return "count_names_first_letter_upcase"
	when "5"
		return "count_names_with_an_uppercase"
	when "6"
		return "count_total_underscores"
	when "7"
		return "alphabetic_order"
	when "8"
		return "get_fifty_littlest"
	when "9"
		return "get_position_epenser"
	when "quit"
		return "quit"
	else
		return "ERROR"
	end 
end

def menu_display
	while (true)
		puts "Bonjour! Choisissez l'option que vous desirez :"
		puts "1 - Compter les journalistes"
		puts "2 - Compter les comptes comportant un numero dans leur nom"
		puts "3 - Compter les comptes contenant le mot 'aude'"
		puts "4 - Compter les comptes commencant par une majuscule"
		puts "5 - Compter les comptes dont le nom contient au moins une majuscule"
		puts "6 - Compter tous les underscores '_' dans les noms de comptes"
		puts "7 - Trier la liste par ordre alphabetique"
		puts "8 - Afficher les 50 noms les plus petits de la liste"
		puts "9 - Indiquer a quelle position se trouve @epenser"
		puts "Pour quitter, tapez 'quit'"
		print " > "
		choice = menu_choice_checker(gets.chomp)
		if (choice == "ERROR")
			puts "Merci de renseigner une demande correcte."
		else
			return (choice)
		end
	end
end
####### END OF MENU PART

def count_total_journalists
	$list.length
end

def count_names_with_number
	count = 0
	for element in $list
		if element =~ /[0123456789]/
			count += 1
		end
	end
	count
end

def count_names_with_aude
	count = 0
	for element in $list
		if element =~ /[a,A][u,U][d,D][e,E]/
			count += 1
		end
	end
	count
end

def count_names_first_letter_upcase
	count = 0
	for element in $list
		if element.slice(1) =~ /[A-Z]/
			count += 1
		end
	end
	return count
end

def count_total_underscores
	count = 0
	for element in $list
		count += element.count "_"
	end
	count
end

def alphabetic_order
	#$list.sort
	for element in $list.sort
		puts element
	end
	return "Juste au dessus"
end

def count_names_with_an_uppercase
	count = 0
	for element in $list
		if element =~ /[A-Z]/
			count += 1
		end
	end
	count
end

def get_fifty_littlest
	puts($list.sort_by { |x| x.length}[1..50])
	return ""
end

def get_position_epenser
	1 + $list.index("@epenser")
end

def perform
	system("clear")
	choice = menu_display()
	system("clear")
	if choice != "quit"
		puts "Resultat : #{send (choice)}"
	end
	puts "Bonne journee !"
end

perform()